import axios from "axios";
export default {
  name: "SearchDoctor",
  data() {
    return {
      mainTag: null,
      doctors: null
    };
  },
  async mounted() {
    this.mainTag = $route.params.id;
    await this.getDoctor(this.mainTag);
  },
  methods: {
    async getDoctor(searchTag) {
      await axios.post('http://localhost:9090/doctors', {
        tag: searchTag
      }).then(response => {
        this.doctors = response;
      });

      // window.location.reload();
    }
  }
};
