import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "AboutView",
  data() {
    return {
      doctors: [],
      create: ''
    };
  },
  async mounted() {
    await this.fetchTags();
  },
  methods: {
    async fetchTags() {
      await axios.get('http://localhost:9090/doctors-all').then(response => {
        let createArr = [{
          specialityName: "create",
          tags: ['create']
        }];
        this.doctors = this.chunk([...createArr, ...response.data.reverse()], 3);
      });
    },
    chunk(arr, chunkSize) {
      if (chunkSize <= 0) throw "Invalid chunk size";
      let R = [];
      for (let i = 0, len = arr.length; i < len; i += chunkSize) R.push(arr.slice(i, i + chunkSize));
      return R;
    }
  }
};
